import React, { useRef, useState } from "react";

interface TabWindowProps {
  title: string;
  zIndex: number;
  onClose: () => void;
  children: React.ReactNode;
}

const TabWindow: React.FC<TabWindowProps> = ({ title, zIndex, onClose, children }) => {
  const windowRef = useRef<HTMLDivElement>(null);
  const [pos, setPos] = useState({ x: 0, y: 0 });
  const [dragging, setDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });

  const onMouseDown = (e: React.MouseEvent) => {
    setDragging(true);
    setDragOffset({
      x: e.clientX - pos.x,
      y: e.clientY - pos.y
    });
  };

  const onMouseMove = (e: MouseEvent) => {
    if (dragging) {
      setPos({
        x: e.clientX - dragOffset.x,
        y: e.clientY - dragOffset.y
      });
    }
  };

  const onMouseUp = () => setDragging(false);

  React.useEffect(() => {
    if (dragging) {
      window.addEventListener("mousemove", onMouseMove);
      window.addEventListener("mouseup", onMouseUp);
    } else {
      window.removeEventListener("mousemove", onMouseMove);
      window.removeEventListener("mouseup", onMouseUp);
    }
    return () => {
      window.removeEventListener("mousemove", onMouseMove);
      window.removeEventListener("mouseup", onMouseUp);
    };
  });

  return (
    <div
      className="terminal-container fixed max-w-3xl max-h-[80vh] z-50"
      style={{
        left: `calc(50% + ${pos.x}px)`,
        top: `calc(50% + ${pos.y}px)`,
        transform: 'translate(-50%, -50%)',
        zIndex,
      }}
      ref={windowRef}
    >
      <div className="scanline"></div>
      <div className="crt-overlay"></div>
      
      <div className="terminal-header px-4 py-3 flex items-center border-b border-green-500/30 cursor-move" onMouseDown={onMouseDown}>
        <div className="text-green-300 text-sm font-light tracking-wide">{title}</div>
        <div className="flex-1"></div>
        <button 
          className="text-green-400 hover:text-green-300 transition-colors text-lg font-bold w-6 h-6 flex items-center justify-center"
          onClick={onClose}
        >
          Ã—
        </button>
      </div>
      
      <div className="terminal-content p-6 overflow-y-auto max-h-[calc(80vh-40px)]">
        {children}
      </div>
    </div>
  );
};

export default TabWindow;
