import React, { useState } from "react";
import MainWindow from "./MainWindow";
import TabWindow from "./TabWindow";
import ProjectsTab from "./ProjectsTab";
import AboutTab from "./AboutTab";
import ExperienceTab from "./ExperienceTab";
import SideQuestsTab from "./SideQuestsTab";
import ContactTab from "./ContactTab";
import ResearchTab from "./ResearchTab";

type TabType = 'projects' | 'about' | 'experience' | 'sidequests' | 'contact' | 'research';

const tabComponents = {
  projects: ProjectsTab,
  about: AboutTab,
  experience: ExperienceTab,
  sidequests: SideQuestsTab,
  contact: ContactTab,
  research: ResearchTab,
};

const Desktop: React.FC = () => {
  const [openTabs, setOpenTabs] = useState<TabType[]>([]);

  const openTab = (tab: TabType) => {
    if (!openTabs.includes(tab)) setOpenTabs([...openTabs, tab]);
  };
  const closeTab = (tab: TabType) => setOpenTabs(openTabs.filter(t => t !== tab));

  return (
    <div className="min-h-screen bg-black">
      <MainWindow openTab={openTab} />
      {openTabs.map((tab, i) => {
        const Component = tabComponents[tab];
        return (
          <TabWindow key={tab} title={tab} zIndex={100 + i} onClose={() => closeTab(tab)}>
            <Component />
          </TabWindow>
        );
      })}
    </div>
  );
};

export default Desktop;
